#include "InputHandler.h"InputHandler* InputHandler::iInstance = 0;InputHandler::InputHandler() : m_keystates(0) {}bool InputHandler::KDown(SDL_Scancode key) {	if (m_keystates != 0)	{		if (m_keystates[key] == 1)		{			return true;		}		else			return false;	}	return false;}void InputHandler::onKDown() {	m_keystates = SDL_GetKeyboardState(0);}void InputHandler::onKUp() {	m_keystates = SDL_GetKeyboardState(0);}void InputHandler :: update () {}